export declare const commitMsg = "#!/bin/bash\n. \"$(dirname \"$0\")/_/husky.sh\"\n\necho $1\nnpx commitlint --edit\n\nmsg=`cat \"$1\"`\nversion=\"\"\n\ncase $msg in\n[*\"major\"*] | [*\"BREAKING CHANGE\"*])\n    version=\"major\"  \n;;\n*\"feat\"*)\n    version=\"minor\"  \n;;\n*)\n    version=\"patch\"\n;;\nesac\n\nnpm version --no-git-tag-version $version\n\nif [ -e \"$PWD/.husky/_post-commit\" ]\nthen\n    mv \"$PWD/.husky/_post-commit\" \"$PWD/.husky/post-commit\"\nfi\nexit 0\n";
